const component: string = "counter"
const version: string = "0"

const Counter = () => {
    const rol = useSelector((state: RootState) => state.auth.rol);

    return (
        <>
            {rol === "BACK" && <CounterBack />}
            {rol === "FRONT" && <CounterFront />}
        </>
    )
}

